import groovy.json.JsonSlurper
import java.text.SimpleDateFormat

def response = context.expand('${Step1_GetProcessorPerformance_Request#Response}')
def jsonResponse = new JsonSlurper().parseText(response)

for (int i = 0; i < jsonResponse.size(); i++) {
	def createTimeUTC = jsonResponse[i].createTimeUTC
	assert jsonResponse[i].containsKey('createTimeUTC'), "createTimeUTC field not found"
	assert createTimeUTC.trim() != "", "createTimeUTC field is empty"

	def dateTimeFormatStr = "yyyy-MM-dd'T'HH:mm:ss.SSS'Z'"
	SimpleDateFormat sdf = new SimpleDateFormat(dateTimeFormatStr);
	boolean isValidDate = false;
	try {
		sdf.parse(createTimeUTC)
		isValidDate = true;
	} catch (Exception ex) {}
	assert isValidDate, "createTimeUTC is not in format: " + dateTimeFormatStr
	}

import groovy.json.JsonSlurper
import java.text.SimpleDateFormat

def response = context.expand('${Step1_GetProcessorPerformance_Request#Response}')
def jsonResponse = new JsonSlurper().parseText(response)

assert jsonResponse.size() > 0, "Response is empty"
for (int i = 0; i < jsonResponse.size(); i++) {
	def jsonObj = jsonResponse[i]
	assert jsonObj.containsKey("cpus"), "cpus not found in response"
	def cpusJsonArr = jsonObj.cpus

	assert cpusJsonArr.size() > 0, "cpus array in response is empty"
	for (int j = 0; j < cpusJsonArr.size(); j++) {
			assert cpusJsonArr[j].containsKey("createTimeUTC"), "createTimeUTC not found"
			def cpusCreateTimeUTC = cpusJsonArr[j].createTimeUTC
			assert cpusCreateTimeUTC != null, "cpus > createTimeUTC field not found"
			assert cpusCreateTimeUTC.trim() != "", "cpus > createTimeUTC field is empty"

			def dateTimeFormatStr = "yyyy-MM-dd'T'HH:mm:ss.SSS'Z'"
			SimpleDateFormat sdf = new SimpleDateFormat(dateTimeFormatStr);
			boolean isValidDate = false;
			try {
				sdf.parse(cpusCreateTimeUTC)
				isValidDate = true;
			} catch (Exception ex) {}
			assert isValidDate, "cpus > createTimeUTC is not in format: " + dateTimeFormatStr
		}
	}

import groovy.json.JsonSlurper

def response = context.expand('${Step1_GetProcessorPerformance_Request#Response}')
def jsonResponse = new JsonSlurper().parseText(response)

assert jsonResponse.size() > 0, "Response is empty"
for (int i = 0; i < jsonResponse.size(); i++) {
	def jsonObj = jsonResponse[i]
	assert jsonObj.containsKey("cpus"), "cpus not found in response"
	def cpusJsonArr = jsonObj.cpus

	assert cpusJsonArr.size() > 0, "cpus array in response is empty"
	for (int j = 0; j < cpusJsonArr.size(); j++) {
			assert cpusJsonArr[j].containsKey("numOfCores"), "cpus > numOfCores not found"
			String cpusNumOfCoresStr = cpusJsonArr[j].numOfCores
			assert cpusNumOfCoresStr.isInteger(), "cpus > numOfCores is not an integer value"
			int cpusNumOfCores = cpusNumOfCoresStr as int
			assert cpusNumOfCores > 0, "cpus > numOfCores is not greater than 0"

			assert cpusJsonArr[j].containsKey("cores"), "cpus > cores not found"
			int cpusCoresArrSize = cpusJsonArr[j].cores.size()
			assert cpusCoresArrSize == cpusNumOfCores, "cpus > numOfCores is not equal to size of cpus > cores array"
		}
	}

